/**
 * Image Adding script.
 *
 * Helps to upload and use the uploaded image.
 */

jQuery(document).ready(function(){var formfield;var uploadID='';jQuery('.upload-button').click(function(){uploadID=jQuery(this).prev('input');jQuery('html').addClass('Image');formfield=jQuery('.upload').attr('name');post_id=jQuery('#post_ID').val();if(typeof post_id==='undefined')post_id=0;tb_show('','media-upload.php?post_id='+post_id+'&amp;type=image&amp;TB_iframe=true');return false});window.original_send_to_editor=window.send_to_editor;window.send_to_editor=function(html){if(formfield){fileurl=jQuery('img',html).attr('src');uploadID.val(fileurl);tb_remove();jQuery('html').removeClass('Image')}else{window.original_send_to_editor(html)}}});